<?php

function solution(string $string) : int {
    $count = 0;
    for ($i=0; $i<strlen($string); $i++) {
        if ($string[$i] === "(") {
            $count++;
        } elseif ($string[$i] === ")") {
            $count--;
        }

        if ($count === -1) {
            return $i + 1;
        }
    }

    return $count;
}

